mapper.delete:
DELETE FROM earner_tax
WHERE earner_code in

	#{earner_code}

AND worker_id = #{worker_id}
AND payment_ym = #{payment_ym};

DELETE FROM input_task
WHERE earner_code in

	#{earner_code}

AND worker_id = #{worker_id}
AND payment_ym = #{payment_ym};

mapper.update:
UPDATE earner_tax 
SET  payment_date = #{payment_date}
	,accrual_ym   = #{accrual_ym}
WHERE tax_id      = #{tax_id}

mapper.insert:
INSERT INTO earner_tax (
	earner_code
	,worker_id
	,payment_ym
	,ins_rate
	)
VALUES (
	 #{earner_code}
	,#{worker_id}
	,#{payment_ym}
	,CASE WHEN #{payment_ym} >= 202207 THEN Round(0.7,2) ELSE Round(0.8, 2) END
)
RETURNING tax_id

mapper.select:
SELECT
	select_date
FROM work_calendar
WHERE worker_id = #{worker_id} 
AND earner_code = #{earner_code}
AND payment_ym  = #{payment_ym}
AND (accrual_ym  = #{payment_ym} or accrual_ym = #{payment_ym}-1)

mapper.delete:
DELETE FROM work_calendar
WHERE earner_code = #{earner_code}
AND accrual_ym    = #{accrual_ym}
