mapper.select:
SELECT code_count
FROM generate_series(#{code_count}, 999999) AS code_count
WHERE NOT EXISTS (
	SELECT 1
	FROM earner
	WHERE earner_code = lpad(code_count::text, 6, '0')
	AND worker_id 	  = #{worker_id}
)
LIMIT 1;

SELECT count(*) as cnt 
FROM earner 
WHERE earner_code = #{custom_code} 
AND     worker_id = #{worker_id}

SELECT *
FROM earner
WHERE earner_code = #{earner_code}
AND   worker_id   = #{worker_id}

mapper.update:
UPDATE worker SET 
	  code_count = CAST(#{earner_code} AS INTEGER)
WHERE worker_id  = #{worker_id}

      UPDATE earner SET 


		      ${param_name} = CAST(#{param_value} AS INTEGER)


		      ${param_name} = CAST(#{param_value} AS DOUBLE PRECISION)


		         ${param_name} = #{param_value}


      WHERE earner_code = #{earner_code}
      AND   worker_id   = #{worker_id}

mapper.delete:
DELETE FROM earner_tax
WHERE earner_code in 

	#{earner_code}

AND   worker_id   = #{worker_id};

DELETE FROM earner
WHERE earner_code in

	#{earner_code}

AND   worker_id   = #{worker_id};
